con:config.script:
// Get RefId from Previous Test Case

import groovy.json.*

// Get Payload from Previous Test Case
def response = context.expand( '${#[TestSuite 1#StaffPeronsal-Multiple#GetStaffPersonals]#Response}' )
def slurped = new JsonSlurper().parseText(response)

// Get RefId from Random Record
def spCount = slurped.StaffPersonals.StaffPersonal.size()
Random random = new Random()
int rndIndex = random.nextInt(spCount)
def refId = slurped.StaffPersonals.StaffPersonal[rndIndex].StaffPersonalRefId

// Set RefId to Property
testRunner.testCase.testSteps["Properties 1"].setPropertyValue( "StaffPersonalRefId", refId )

// Update Payload by Adding Dynamic Values to Some Fields
import groovy.json.*

// Load Data
def dynamicId = context.expand( '${GenDynamicId#DynamicId}' )
def response = context.expand( '${GetStaffPersonal#Response}' )
def slurped = new JsonSlurper().parseText(response)

// Update Fields
def givenName = slurped.StaffPersonal.PersonInfo.Name.GivenName
slurped.StaffPersonal.PersonInfo.Name.GivenName = "$givenName" + "$dynamicId"
slurped.StaffPersonal.remove("RecordAudit")

// Write Updated Payload to Result
return new JsonBuilder(slurped)

// Update Payload by Adding Dynamic Values to Some Fields
import groovy.json.*

// Load Data
